SELECT c_custkey, sum(lo_revenue) AS lo_revenue FROM lineorder, customer, dates WHERE lo_custkey = c_custkey AND d_year = 1993 GROUP BY c_custkey ORDER BY lo_revenue DESC;
SELECT s_suppkey, sum(lo_revenue) AS lo_revenue FROM lineorder, supplier , dates WHERE lo_suppkey = s_suppkey AND d_datekey BETWEEN 19930101 AND 19931231 GROUP BY s_suppkey ORDER BY lo_revenue DESC;
SELECT d_year, d_month, p_category, sum(lo_revenue) AS lo_revenue FROM lineorder, dates, part WHERE lo_orderdate = d_datekey AND lo_partkey = p_partkey AND d_year BETWEEN 1992 AND 1997 GROUP BY d_year, d_month, p_category ORDER BY d_year, d_month, p_category;
SELECT p_category, avg(lo_quantity) AS avg_quantity FROM lineorder, part, customer WHERE lo_partkey = p_partkey AND lo_custkey = c_custkey AND c_region = 'ASIA' AND p_category = 'MFGR14' GROUP BY p_category;
SELECT p_brand, s_suppkey, d_year, sum(lo_revenue) AS lo_revenue FROM lineorder, part, supplier, dates WHERE lo_partkey = p_partkey AND lo_suppkey = s_suppkey AND lo_orderdate = d_datekey GROUP BY p_brand, s_suppkey, d_year ORDER BY p_brand, s_suppkey, d_year;
SELECT c_region, s_region, count(*) AS num_orders FROM lineorder, customer, supplier WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey GROUP BY c_region, s_region;
SELECT c_custkey, count(*) AS num_orders, d_year, d_weeknuminyear FROM lineorder, customer, dates WHERE lo_custkey = c_custkey AND lo_orderdate = d_datekey GROUP BY c_custkey, d_year, d_weeknuminyear ORDER BY c_custkey, d_year, d_weeknuminyear;
SELECT s_suppkey, sum(lo_revenue) AS lo_revenue FROM lineorder, supplier WHERE lo_suppkey = s_suppkey GROUP BY s_suppkey ORDER BY lo_revenue DESC;
SELECT d_year, sum(CASE WHEN d_weeknuminyear <= 13 THEN lo_revenue ELSE 0 END) AS pre_promotion_revenue, sum(CASE WHEN d_weeknuminyear > 13 THEN lo_revenue ELSE 0 END) AS post_promotion_revenue FROM lineorder, dates WHERE lo_orderdate = d_datekey AND d_year = 1997 GROUP BY d_year;
SELECT p_category, c_region, c_nation, c_city, sum(lo_revenue) AS lo_revenue FROM lineorder, part, customer WHERE lo_partkey = p_partkey AND lo_custkey = c_custkey GROUP BY p_category, c_region, c_nation, c_city ORDER BY p_category, c_region, c_nation, c_city;